        <SelectContent>
          {CURRENCIES.map((currency) => (
            <SelectItem
              key={currency.symbol}
              value={currency.symbol}
              data-testid={`option-currency-${currency.code}`}
            >
              {currency.symbol} - {currency.name}
            </SelectItem>
          ))}
        </SelectContent>
      </Select>
    </div>
  );
}

